const  Calc=()=>{
    const[ num ,addNum]=useState(0);


    const onAdd=()=>{
        addNum(num)


    }


   return(
       <>
       <div className="Calc">
           <span>Result: {num}</span>
           <button onClick={onAdd()}>Add</button>

       </div>
       </>

   )
}

export default Calc;
