import React, { useState } from 'react';

const Bg=()=>
{
    const color="purple";
    const name="click me"
    const [bg,setBg]=useState(color);

    const bgChange=()=>{
        let newBg="blue";
        setBg(newBg);

    }

    return(
        <>
        <div style={{backgroundColor: bg}}>
            <button onClick={bgChange}>{name}</button>

        </div>
        </>
    )
}

export default Bg;
